import { useState,React } from 'react';
import { Link } from 'react-router-dom';
import { ProSidebar, Menu, MenuItem, SubMenu,SidebarHeader,SidebarFooter,SidebarContent } from 'react-pro-sidebar';
import 'react-pro-sidebar/dist/css/styles.css';
import { FaList, FaRegHeart } from "react-icons/fa";
import { FiHome, FiLogOut, FiArrowLeftCircle, FiArrowRightCircle } from "react-icons/fi";
import { RiPencilLine } from "react-icons/ri";
import { BiCog } from "react-icons/bi";
import {headercss} from "../../header.css";

function DashboardUser() {

    //create initial menuCollapse state using useState hook
    const [menuCollapse, setMenuCollapse] = useState(false)

    //create a custom function that will change menucollapse state from false to true and true to false
  const menuIconClick = () => {
    //condition checking to change state from true to false and vice versa
    menuCollapse ? setMenuCollapse(false) : setMenuCollapse(true);
  };

  


    return (
        <>
      <div id="header">
          {/* collapsed props to change menu size using menucollapse state */}
        <ProSidebar collapsed={menuCollapse}>
          <SidebarHeader>
          <div className="logotext">
              {/* small and big change using menucollapse state */}
              <p>{menuCollapse ? "Logo" : "Big Logo"}</p>
            </div>
            <div className="closemenu" onClick={menuIconClick}>
                {/* changing menu collapse icon on click */}
              {menuCollapse ? (
                <FiArrowRightCircle/>
              ) : (
                <FiArrowLeftCircle/>
              )}
            </div>
          </SidebarHeader>
          <SidebarContent>
            <Menu iconShape="square">
              <MenuItem onClick={this.activeid} data-activeid="dashboard-main" active={true} icon={<FiHome />}>
                Dashboard
              </MenuItem>
              <MenuItem onClick={this.activeid} data-activeid="credential-setup" icon={<FaList />}>credential Setup</MenuItem>
              <MenuItem onClick={this.activeid} data-activeid="issuer-dids" icon={<FaRegHeart />}>Issuer DIDs</MenuItem>
              <MenuItem onClick={this.activeid} data-activeid="ecosystem" icon={<RiPencilLine />}>Ecosystem Management</MenuItem>
              <MenuItem icon={<BiCog />}>Settings</MenuItem>
            </Menu>
          </SidebarContent>
          <SidebarFooter>
            <Menu iconShape="square">
              <MenuItem icon={<FiLogOut />}>Logout</MenuItem>
            </Menu>
          </SidebarFooter>
        </ProSidebar>
        
      </div>
      <div class="full-content">
        <div id="dashboard-main" className='dashboard-show'>Main Dashboard</div>
        <div id="issuer-dids" className='dashboard-show hide-content'>Main Dashboard</div>
        <div id="ecosystem" className='dashboard-show hide-content'>Main Dashboard</div>
        
    </div>
    </>
        );
}
export default DashboardUser;
